import React,{useEffect,useState} from "react";
import "./css/style.css";
const Tempapp = ()=>{
    const [city,setcity]=useState(null);
    const [search,setsearch]=useState("Lucknow");
    useEffect(()=>{
        const fetchapi =async()=>{
            const url=`http://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=ef12e9802e626118768c030e1c02d42a`
            const response=await fetch(url);
            const resjson=await response.json();
            setcity(resjson.main);
        }
        fetchapi();

    },[search])
    return(
        <>
        <div className="box">
            <div className="inputData">
                <input
                type="search"
                className="inputField"
                    onChange={(event)=>{
                        setsearch(event.target.value)

                    }}
                />
            </div>
            {
                !city?(
                    <p>No Data Found</p>
                ):(
                    <div>

            <div className="info">
            <div id="whitebox">
                <h1 className="location">

                <i class="fa fa-street-view"> </i>{search}
                </h1>

                <h1 className="temp"> {city.temp}°C

                </h1>

                <h3 className="tempin_max">Min : {city.temp_min}°C | Max: {city.temp_max}°C</h3>
                </div>
            </div>
        

            <div className="wave-one"></div>
            <div className="wave-two"></div>
            <div className="wave-three"></div>
        </div>

                )
            }
            </div>
        
        </>
    )
}
export default Tempapp;
