import React,{useEffect,useState} from "react";
import "./css/style.css";
const Tempapp = ()=>{
    const [city,setcity]=useState(null);
    const [search,setsearch]=useState("Lucknow");
    const currDate=new Date().toLocaleDateString();
    var d=new Array(7);
    d[0]="SUN";
    d[1]="MON";
    d[2]="TUE";
    d[3]="WED";
    d[4]="THU";
    d[5]="FRI";
    d[6]="SAT";
    var month = new Array();
    month[0] = "JAN";
    month[1] = "FEB";
    month[2] = "MAR";
    month[3] = "APR";
    month[4] = "MAY";
    month[5] = "JUN";
    month[6] = "JUL";
    month[7] = "AUG";
    month[8] = "SEPT";
    month[9] = "OCT";
    month[10] = "NOV";
    month[11] = "DEC";
    let currentime=new Date();
    let finaldate=currentime.getDate()
    let finalmonth=month[currentime.getMonth()];
    let finalday=d[currentime.getDay()];

    useEffect(()=>{
        const fetchapi =async()=>{
            const url=`http://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=ef12e9802e626118768c030e1c02d42a`
            const response=await fetch(url);
            const resjson=await response.json();
            setcity(resjson.main);
        }
        fetchapi();

    },[search])

    return(
        <>
        <div className="box">
            <div className="inputData">
                <input
                type="search"
                className="inputField"
                    onChange={(event)=>{
                        setsearch(event.target.value)

                    }}
                />
            </div>
            {
                !city?(
                    <p>No Data Found</p>
                ):(
                    <div>

            <div className="info">
            <div id="whitebox">
                <h1>{finalday}, {finaldate} {finalmonth} </h1>

                <h1 className="temp"> {city.temp}°C

                </h1>

                <h3 className="tempin_max">Min : {city.temp_min}°C | Max: {city.temp_max}°C</h3>
                <h1 className="location" placeholder="Search......">

<i class="fa fa-street-view"> </i>{search}
</h1>
                </div>
            </div>
        

            <div className="wave-one"></div>
            <div className="wave-two"></div>
            <div className="wave-three"></div>
        </div>

                )
            }
            </div>
        
        </>
    )
}
export default Tempapp;
