import React,{useEffect,useState} from "react";
import "./css/style.css";
const Tempapp = ()=>{
    const [city,setcity]=useState(null);
    const [search,setsearch]=useState("Lucknow");
    const currDate=new Date().toLocaleDateString();
    var d=["SUN","MON","TUE","WED","THU","FRI","SAT"];
    var month=["Jan","Feb","March","April","May","Jun","July","Aug","Sept","Oct","Nov","Dec"];
    let currentime=new Date();
    let currday=d[currentime.getDay()];
    let currdate=currentime.getDate();
    let currmonth=month[currentime.getMonth()];


    //useEffect(()=>{
        const fetchapi =async()=>{
            const url=`http://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=ef12e9802e626118768c030e1c02d42a`
            const response=await fetch(url);
            const resjson=await response.json();
            setcity(resjson);

        }
        console.log(city);
        //fetchapi();

    //},[search])
    useEffect(()=>{
        fetchapi();

    },[]);

    return(
        <>
        <div className="box">
            <div className="inputData">
                <input
                type="search"
                className="inputField"
                placeholder="Search..."
                    onChange={(event)=>{
                        setsearch(event.target.value)


                    }}
                />
            </div>
            {
                !city?(
                    <h1 id="error">Sorry No Data Found !</h1>
                ):(
                    <div>

            <div className="info">
            <div id="whitebox">
                <h2>{currday},{currdate} {currmonth}</h2>
                 {
                     city.map((x)=>{
                         return (
                             <>
                             <h1 className="temp"> {x.main.temp}°C

</h1>
                             </>
                         )

                     })
                 }


                <h3 className="tempin_max">Min : city.temp_min°C | Max: city.temp_max°C</h3>
                <h1 className="location">

<i class="fa fa-street-view"> </i>{search}
</h1>
                </div>
            </div>
        

            <div className="wave-one"></div>
            <div className="wave-two"></div>
            <div className="wave-three"></div>
        </div>

                )
            }
            </div>
        
        </>
    )
}
export default Tempapp;
